import './App.css';
import { useState } from 'react';

function App() {
  const [count, setCount] = useState(0);
  const [inputvalue, Setinputvalue] = useState('');

  return (
    <div className="App">
      <h1>Counter: {count}</h1>
      <button onClick={() => setCount(count + 10)} className="button"> Add </button>
      <button onClick={() => setCount(count - 5)} className="button"> Sub </button>
      <div>
        <input type='text' value={inputvalue} onChange={(e) => Setinputvalue(e.target.value)} />
        <p>{inputvalue}</p>
     </div>
    </div>
  );
}

export default App;
