import './App.css';
import { useState } from 'react';

function App() {
  const [count, setCount] = useState(0);
  const [inputvalue, setInputValue] = useState('');

  return (
    <div className="App">
      <h1>Counter: {count}</h1>
      <button onClick={() => setCount(count + 10)} className="button"> Add </button>
      <button onClick={() => setCount(count - 5)} className="button"> Sub </button>
      <input type='text' value={inputvalue} onChange={(e) => setInputValue(e.target.value)} />
      <p>{inputvalue}</p>
    </div>
  );
}

export default App;
